import React, { Component } from "react"
import Container from '../Components/Container/Container'
import api from "../Utils/api"
class Search extends Component {
    
    // componentDidMount() {
    //     api.getGooglebooks("love+in+the+time+of+cholera").then
    // }


    handleInputChange = (event) => {
   const {name, value} = event.target
        this.setState({
            [name]: value
        })
    } 

    handleFormSubmit = (event) => {
     
         api.getGooglebooks(this.state.keyword).then((response) => {
             console.log(response)
            this.setState({googleBooks: response.data})
             console.log(this.state.googleBooks)
         })
    }

    render(){
        return(
            <Container  booksArray = {this.state.googleBooks}   keyword = {this.state.keyword}    handleInputChange = {this.handleInputChange} handleFormSubmit = {this.handleFormSubmit} />
        )
    }
}

export default Search
