import React, { Component } from "react"
import Container from '../Components/Container/Container'
import api from "../Utils/api"
class Search extends Component {
    state = {
        googleBooks : [],
        keyword : ""
    }

    // componentDidMount() {
    //     api.getGooglebooks("love+in+the+time+of+cholera").then
    // }


    handleInputChange = (event) => {
   const {name, value} = event.target
        this.setState({
            [name]: value
        })
    } 

    handleFormSubmit = (event) => {


         api.getGooglebooks(this.state.keyword).then()
    }

    render(){
        return(
            <Container     keyword = {this.state.keyword}    handleInputChange = {this.handleInputChange} handleFormSubmit = {this.handleFormSubmit} />
        )
    }
}

export default Search
